package de.beiertu.kafka.protobuf.example

import de.beiertu.protobuf.Person

class App {
    val greeting: String
        get() {
            return "Hello world."
        }
}

fun main() {
    val person = Person.newBuilder()
        .setName("Peter")
        .setAge(25)
        .setGender(Person.Gender.MALE)
        .build()
    println("hello $person")
}
